import Layout from "../../components/Layout"
import { Box, Heading, Divider, HStack, Image, Text } from '@chakra-ui/core'
import { css } from '@emotion/core'

const DetailContainer = css`
  & > p {
    font-size:14px;
    margin-bottom:10px;
  }
  & > img {
    margin-bottom:10px;
    display:block;
  }
`

export default function Detail () {
  return (<Layout>
    <Box maxW={1200} mx="auto" mt="70px">
      <Heading as="h2" Size="xl">主标题</Heading>
      <Heading mt="10px" as="h4" Size="lg" color="gray.500" fontWeight="light">副标题</Heading>
      <Divider mt="10px" />
      <Box overflow="hidden" mt="10px">
        <Text float="left">作者：Tomas</Text>
        <Text float="right">发布时间：2045-05-25</Text>
      </Box>
      <Divider mt="10px" />
      <Box overflow="hidden" mt="10px">
        <p>文章内容</p>
      </Box>
    </Box>
  </Layout>)
}
