import Layout from "../../components/Layout"
import { Box, Heading, Divider, HStack, Image, Text } from '@chakra-ui/core'
import { css } from '@emotion/core'
import axios from 'axios'
import { baseURL } from "../../axiosConfig"

const DetailContainer = css`
  padding:10px;
  & > p {
    font-size:14px;
    margin-bottom:10px;
  }
  & > img {
    margin-bottom:10px;
    display:block;
  }
`

export default function Detail ({detail}) {
  console.log(detail)
  return (<Layout>
    <Box maxW={1200} mx="auto" mt="70px">
      <Heading as="h2" Size="xl">{detail.title}</Heading>
      <Heading 
        mt="10px" 
        as="h4" 
        Size="lg" 
        color="gray.500" 
        fontWeight="light"
      >
        {detail.sub}
      </Heading>
      <Divider mt="10px" />
      <Box overflow="hidden" mt="10px">
        <Text float="left">作者：Tomas</Text>
        <Text float="right">发布时间：2045-05-25</Text>
      </Box>
      <Divider mt="10px" />
      <Box mt="10px" css={DetailContainer}>
        <p>文章内容</p>
        <p>文章内容</p>
        <p>文章内容</p>
        <p>文章内容</p>
      </Box>
    </Box>
  </Layout>)
}

export async function getStaticPaths (){
  //接收到的格式：["1","2"]
  let { data } = await axios.get('/api/videos',{ baseURL })
  //转换成：[{params:{id:"1"}}]
  let paths = data.map(id=>({params:{id}}))
  return{
    paths,
    fallback: false
  }
}

export async function getStaticProps ({params}){
  let id = params.id
  let { data: detail } = await axios.get(`/api/detail?id=${id}`,{ baseURL })
  return {
    props:{
      detail
    }
  }
}
